import React, {Component} from 'react' ;
import {Redirect} from 'react-router-dom';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import { Map, TileLayer, Marker, Popup,CircleMarker } from 'react-leaflet';
import mock from './data';
//import RegionInfo from './regionInfo';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import useMediaQuery from '@material-ui/core/useMediaQuery';
import { useTheme } from '@material-ui/core/styles';


//const rows = mock.WorldData;


const useStyles = makeStyles((theme) => ({
  button: {
    margin: theme.spacing(2),
  },
}));


const handleAddZone = () => {
  return <Redirect to="/AgentDeSante/addZone/addZone" />
  
}

export default function DisplayMap(props) {
  
  var { regions } = props;
  const [open, setOpen] = React.useState(false);
  const theme = useTheme();
  const fullScreen = useMediaQuery(theme.breakpoints.down('sm'));
  const classes = useStyles;

  let myIcon = L.icon({
    iconUrl:'data:image/png;base64,',
    iconSize : [25, 40],
    iconAnchor : [12, 40],
    popupAnchor : [0, -40]
  })

  const style = {
    width: '100%',
    height: '70vh'
  }

  const state = {
    lat: 40.5545,
    lng: -89.61,
    zoom: 2,
  }

  const position = [state.lat, state.lng];

    const handleClickOpen = () => {
      setOpen(true);
    };
  
    const handleClose = () => {
      setOpen(false);
    };

   const onClickMarker = () => {
    return (
      <div>
        <Button variant="outlined" color="primary" onClick={handleClickOpen}>
          Open responsive dialog
        </Button>
        <Dialog
          fullScreen={fullScreen}
          open={open}
          onClose={handleClose}
          aria-labelledby="responsive-dialog-title"
        >
          <DialogTitle id="responsive-dialog-title">{"Use Google's location service?"}</DialogTitle>
          <DialogContent>
            <DialogContentText>
              Let Google help apps determine location. This means sending anonymous location data to
              Google, even when no apps are running.
            </DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button autoFocus onClick={handleClose} color="primary">
              Disagree
            </Button>
            <Button onClick={handleClose} color="primary" autoFocus>
              Agree
            </Button>
          </DialogActions>
        </Dialog>
      </div>
    );
    }
  



    return (
      <div>
        <Button
        variant="contained"
        color="primary"
        block
        //onclick = {handleAddZone}
        //className={classes.button}
        startIcon={<AddIcon />}
      >
        Ajouter une zone
      </Button>
       <br/><br/>
        <Map center={position} 
        zoom={state.zoom}
        style={style}
        >
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {regions.map((row,index) => {
            return(
            <CircleMarker center={[row.latitude,row.longitude]} 
                    color="red" radius={row.radius}
                    icon={myIcon}
                    onclick={onClickMarker}>
            <Popup>
            <h6>{row.name}</h6>
            <p>Confirmés : {row.statistic.confirmed_infected_total} </p>
            <p>Décès : {row.statistic.deaths}</p>
            <p>cas suspects: {row.statistic.suspicious_cases}</p>
            <p>cas asymptomatiques : {row.statistic.asymptomatic_cases}</p>
            </Popup>
          </CircleMarker>
          )
          })}
          
        </Map>
      </div>
    )
  }






      
      
    